object W1L3_PATTERNMATCHING extends App {
//  val sequence=""
//  val motif="GGGAGAGG"

  val sequence = io.Source.fromFile("/home/jonas/Downloads/Vibrio_cholerae.txt").getLines().mkString("")
  val motif = "CTTGATCAT"
  val splitted:List[String] = sequence.sliding(motif.length).toList
  val indexes:List[Int] = for ( i <- List.range(0,splitted.size)
                                if splitted(i).equals(motif) )
  yield i
  println(indexes.mkString(" "))
}
