object W2L2_PEPTIDE extends App {

  val sequence = ""
  val peptide = "WEVSPCWA"

  val fragments = sequence.sliding(peptide.size * 3)


    val dnas = for (frag <- fragments;
                    if (RNATranslator.codeProtein(new DNA("unn",frag).produceRNA().sequence).equals(peptide) || RNATranslator.codeProtein(new DNA("unn",frag).reverseComplement.produceRNA().sequence).equals(peptide) ))
                    println(frag)

}
