object W7L2_FITTING_ALIGNMENT_PROBLEM extends App {
  val v = ""
  val w = "CTACGTAGCCCGTTCGTCATGCCGGGTGAGTTAAGGCGTCCGTAATGGTAGTCTGTTTTTTTGCAAAGTATGGTAACCTAG"

  //  println(scores.mkString("\n"))

  val graph = new FittingAlignmentGraph(v,w,1,1,1)
  graph.printData()
  val maxNode = graph.getEndNode()
  graph.output(maxNode._1,maxNode._2,List(),List())
  println(maxNode)

}
